// Mortgage Calculator!

// Imports:
import java.text.NumberFormat;
import java.util.Scanner;

public class Main {

    public static float validate(String query, int minLimit, int maxLimit) {

        // Instantiating an object of the Scanner class & Instantiating the input value from the user:
        Scanner takeInput = new Scanner(System.in);
        float value;

        while (true){

            System.out.print(query);
            value = takeInput.nextFloat();

            if  (value < minLimit || value > maxLimit)
                    System.out.printf("Enter a valid number between %d and %d!\n", minLimit, maxLimit);
            else
                return value;
        }
    }

    public static void main(String[] args) {
        final byte percent = 100;
        final byte months = 12;

        float principal = validate("Principal ($1K - $1M): ", 1000, 1_000_000);
        float annualInterestRate = validate("Annual Interest Rate: ", 0, 30);
        float period = validate("Period (Years): ", 0, 30);

        // Calculating the Mortgage:
        annualInterestRate /= (percent * months);
        period *= months;

        // Formula:
        double mortgage = principal *
                (annualInterestRate * Math.pow(1 + annualInterestRate, period)) /
                (Math.pow(1 + annualInterestRate, period) - 1);

        // Display the calculated Mortgage:
        String displayMortgage = NumberFormat.getCurrencyInstance().format(mortgage);
        System.out.println("Total Mortgage: " + displayMortgage);


    }

}

